import {React, useRef, useEffect} from "react";

const EntradaTexto = ({ label, name, value, onChange, type, placeholder, error }) => {
    return (
        <div>
            <label className="nombreCampo">{label}</label>
            <input
                className="campoTexto"
                name={name}
                value={value}
                onChange={onChange}
                type={type}
                placeholder={placeholder}
            />
            {error && <div className="error">{error}</div>}
        </div>
    );
};

const EntradaTextoArea = ({ label, name, value, onChange, placeholder, error }) => {
    const textareaRef = useRef(null);

    // Ajustar la altura del textarea dinámicamente
    useEffect(() => {
        if (textareaRef.current) {
            textareaRef.current.style.height = 'auto'; // Reseteamos la altura
            textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`; // Ajustamos la altura según el contenido
        }
    }, [value]); // Se ejecuta cada vez que 'value' cambia

    const handleChange = (e) => {
        // Llamamos al onChange para actualizar el valor
        onChange(e);
    };

    return (
        <div>
            <label className="nombreCampo">{label}</label>
            <textarea
                ref={textareaRef}
                className="campoTexto"
                name={name}
                value={value}
                onChange={handleChange} // Manejamos el cambio de texto
                placeholder={placeholder}
                rows="3" // Puedes ajustar la cantidad inicial de filas
                style={{ resize: "none", overflowY: "hidden", width: "100%" }} // Evitar que el usuario cambie el tamaño manualmente
            />
            {error && <div className="error">{error}</div>}
        </div>
    );
};

const EntradaRadio = ({ label, name, options, value, onChange, error }) => {
    return (
        <div>
            <label className="nombreCampo">{label}</label>
            <div className="opciones">
                {options.map((option) => (
                    <div key={option.value} className="opcion">
                        <input
                            type="radio"
                            id={`${name}_${option.value}`}
                            name={name}
                            value={option.value}
                            checked={value === option.value}
                            onChange={onChange}
                        />
                        <label htmlFor={`${name}_${option.value}`}>{option.label}</label>
                    </div>
                ))}
            </div>
            {error && <div className="error">{error}</div>}
        </div>
    );
};

const EntradaSelect = ({ label, name, value, onChange, error, options }) => {
    return (
        <div>
            <label className="nombreCampo">{label}</label>
            <select name={name} onChange={onChange} value={value}>
                <option value="">Selecciona una opción</option>
                {options.map(option => (
                    <option key={option.value} value={option.value}>
                        {option.label}
                    </option>
                ))}
            </select>
            {error && <div className="error">{error}</div>}
        </div>
    );
};

export { EntradaTexto, EntradaTextoArea, EntradaRadio, EntradaSelect };
