import React from "react";

const EntradaTexto = ({ label, name, value, onChange, type, placeholder, error }) => {
    // Ajustar la altura del textarea automáticamente
    const handleInputChange = (e) => {
        const textarea = e.target;

        // Reseteamos la altura para que el campo se ajuste al contenido
        textarea.style.height = 'auto';
        
        // Ajustamos la altura según el contenido
        textarea.style.height = `${textarea.scrollHeight}px`;

        // Llamamos a la función onChange que pasas como prop
        onChange(e);
    };

    return (
        <div>
            <label className="nombreCampo">{label}</label>
            {type === "text" ? (
                // Usamos un textarea si el tipo es 'text'
                <textarea
                    className="campoTexto"
                    name={name}
                    value={value}
                    onChange={handleInputChange} // Usamos nuestra función para ajustar la altura
                    placeholder={placeholder}
                    rows="3" // Altura inicial mínima
                    style={{ resize: "none", overflowY: "hidden", width: "100%" }} // Sin redimensionar y sin scrollbar
                />
            ) : (
                // Para otros tipos de input como 'number', 'email', etc., usamos un input normal
                <input
                    className="campoTexto"
                    name={name}
                    value={value}
                    onChange={onChange}
                    type={type}
                    placeholder={placeholder}
                />
            )}
            {error && <div className="error">{error}</div>}
        </div>
    );
};

const EntradaRadio = ({ label, name, options, value, onChange, error }) => {
    return (
        <div>
            <label className="nombreCampo">{label}</label>
            <div className="opciones">
                {options.map((option) => (
                    <div key={option.value} className="opcion">
                        <input
                            type="radio"
                            id={`${name}_${option.value}`}
                            name={name}
                            value={option.value}
                            checked={value === option.value}
                            onChange={onChange}
                        />
                        <label htmlFor={`${name}_${option.value}`}>{option.label}</label>
                    </div>
                ))}
            </div>
            {error && <div className="error">{error}</div>}
        </div>
    );
};

const EntradaSelect = ({ label, name, value, onChange, error, options }) => {
    return (
        <div>
            <label className="nombreCampo">{label}</label>
            <select name={name} onChange={onChange} value={value}>
                <option value="">Selecciona una opción</option>
                {options.map(option => (
                    <option key={option.value} value={option.value}>
                        {option.label}
                    </option>
                ))}
            </select>
            {error && <div className="error">{error}</div>}
        </div>
    );
};

export { EntradaTexto, EntradaRadio, EntradaSelect };
