import React from "react";
import Swal from "sweetalert2";

const MensajeCorrecto = ({ icon, title, text, confirmButtonText }) => {
    React.useEffect(() => {
        Swal.fire({
            icon: "success",
            title: {title},
            text: {text},
            confirmButtonText: "Vale"
        });
    }, [icon, title, text, confirmButtonText]);

    return null;

};

const MensajeError = ({ icon, title, text, confirmButtonText }) => {
    React.useEffect(() => {
        Swal.fire({
            icon: "error",
            title: "Error",
            text: "Hay algunos campos con errores en el formulario",
            confirmButtonText: "Vale"
        });
    }, [icon, title, text, confirmButtonText]);

    return null;

};

const MensajeConexion = ({ icon, title, text, confirmButtonText }) => {
    React.useEffect(() => {
        Swal.fire({
            icon: "question",
            title: "Error de Conexión",
            text: "Verifica tu conexión a internet o inténtalo de nuevo",
            confirmButtonText: "Vale"
        });
    }, [icon, title, text, confirmButtonText]);

    return null;

};





export {MensajeCorrecto, MensajeError, MensajeConexion}
