import React from "react";
import Swal from "sweetalert2";

const MensajeOk = ({ title, text }) => {
    React.useEffect(() => {
        Swal.fire({
            icon: "success",
            title: title,
            text: text,
            confirmButtonText: "Vale"
        });
    }, [title, text]);

    return null;

};

const MensajeError = () => {
    React.useEffect(() => {
        Swal.fire({
            icon: "error",
            title: "Error",
            text: "Hay algunos campos con errores en el formulario",
            confirmButtonText: "Vale"
        });
    }, []);

    return null;

};

const MensajeConexion = () => {
    React.useEffect(() => {
        Swal.fire({
            icon: "question",
            title: "Error de Conexión",
            text: "Verifica tu conexión a internet o inténtalo de nuevo",
            confirmButtonText: "Vale"
        });
    }, []);

    return null;

};

export { MensajeOk, MensajeError, MensajeConexion }
